import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function ProductDetail() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Fetch product data using the product ID
    const fetchProduct = async () => {
      try {
        const response = await fetch(`http://localhost:3001/products/${id}`);
        const productData = await response.json();
        setProduct(productData);
      } catch (error) {
        console.error("Error fetching product data", error);
      }
    };

    fetchProduct();
  }, [id]);

  if (!product) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>{product.title}</h2>
      <p>Brand: {product.brand}</p>

      <div class="container my-5">
        <div class="row">
          <div class="col-md-5">
            <div class="main-img">
              <img
                class="img-fluid"
                src="https://cdn.pixabay.com/photo/2015/07/24/18/40/model-858753_960_720.jpg"
                alt="ProductS"
              />
            </div>
          </div>
          <div class="col-md-7">
            <div class="main-description px-2">
              <div class="category text-bold">Category: Women</div>
            </div>

            <div class="row questions bg-light p-3">
              <div class="col-md-1 icon">
                <i class="fa-brands fa-rocketchat questions-icon"></i>
              </div>
              <div class="col-md-11 text">
                Have a question about our products at E-Store? Feel free to
                contact our representatives via live chat or email.
              </div>
            </div>

            <div class="delivery my-4">
              <p class="font-weight-bold mb-0">
                <span>
                  <i class="fa-solid fa-truck"></i>
                </span>{" "}
                <b>Delivery done in 3 days from date of purchase</b>{" "}
              </p>
              <p class="text-secondary">
                Order now to get this product delivery
              </p>
            </div>
            <div class="delivery-options my-4">
              <p class="font-weight-bold mb-0">
                <span>
                  <i class="fa-solid fa-filter"></i>
                </span>{" "}
                <b>Delivery options</b>{" "}
              </p>
              <p class="text-secondary">View delivery options here</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
