import React, { useEffect, useState } from "react";
import Item from "./Item";

function Products() {
  const [list, setList] = useState([]);
  //get all items
  const getList = async () => {
    try {
      const response = await fetch("http://localhost:3001/products", {
        method: "GET",
        headers: { "Content-Type": "application/json" },
      });
      console.log(response);
      const jsonDate = await response.json();
      console.log(jsonDate);
      setList(jsonDate);
    } catch (error) {
      console.log(error.message);
    }
  };
  useEffect(() => {
    getList();
  }, []);

  return (
    <div>
      <h2>Products</h2>

      {list.map((item, index) => (
        <Item
          key={index}
          title={item.title}
          link={item.link}
          alt={item.title}
        />
      ))}
      <div class="container py-5">
        <div class="row">
          <div class="col-lg-8 mx-auto">
            <ul class="list-group shadow">
              <li class="list-group-item">
                <div class="media align-items-lg-center flex-column flex-lg-row p-3">
                  <div class="media-body order-2 order-lg-1">
                    <h5 class="mt-0 font-weight-bold mb-2">Awesome product</h5>
                    <p class="font-italic text-muted mb-0 small">
                      Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                      Suscipit fuga autem maiores necessitatibus.
                    </p>
                    <div class="d-flex align-items-center justify-content-between mt-1">
                      <h6 class="font-weight-bold my-2">$120.00</h6>
                    </div>
                  </div>
                  <img
                    src="https://bootstrapious.com/i/snippets/sn-cards/shoes-1_gthops.jpg"
                    alt="Generic placeholder image"
                    width="200"
                    class="ml-lg-5 order-1 order-lg-2"
                  />
                </div>
              </li>

              <li class="list-group-item">
                <div class="media align-items-lg-center flex-column flex-lg-row p-3">
                  <div class="media-body order-2 order-lg-1">
                    <h5 class="mt-0 font-weight-bold mb-2">Awesome product</h5>
                    <p class="font-italic text-muted mb-0 small">
                      Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                      Suscipit fuga autem maiores necessitatibus.
                    </p>
                    <div class="d-flex align-items-center justify-content-between mt-1">
                      <h6 class="font-weight-bold my-2">$99.00</h6>
                    </div>
                  </div>
                  <img
                    src="https://bootstrapious.com/i/snippets/sn-cards/shoes-2_g4qame.jpg"
                    alt="Generic placeholder image"
                    width="200"
                    class="ml-lg-5 order-1 order-lg-2"
                  />
                </div>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Products;
