import React, { useState } from "react";
import Outputtable from "./Outputtable";
import Safealert from "./SafeAlert";

function InputArea() {
  const [formData, setFormData] = useState({
    content: "",
    output: "",
    isTable: false,
    isSafe: false,
  });

  function handleChange(event) {
    const newValue = event.target.value.toLowerCase();
    setFormData((prevData) => ({
      ...prevData,
      content: newValue,
    }));
    // Check if the input is cleared and reload the window
    if (newValue.trim() === "") {
      window.location.reload();
    }
  }

  async function checkIng(event) {
    event.preventDefault();

    const unsafeInglist = [
      "oxybenzone",
      "octinoxate",
      "octocrylene",
      "homosalate",
      "parabens",
      "triclosan",
      "para aminobenzoic acid",
      "methylbenzylidene camphor",
      "avobenzone",
    ];

    let unsafeIngredients = [];

    for (let ingredient of unsafeInglist) {
      if (formData.content.includes(ingredient)) {
        unsafeIngredients.push(ingredient);
      }
    }

    if (unsafeIngredients.length > 0) {
      setFormData((prevData) => ({
        ...prevData,
        isTable: true,
      }));
    } else {
      setFormData((prevData) => ({
        ...prevData,
        isSafe: true,
      }));
    }

    setFormData((prevData) => ({ ...prevData, output: unsafeIngredients }));
  }

  return (
    <div class="row justify-content-center">
      <div class="col-md-6 mt-5 ">
        <form>
          <div class="mb-3">
            <textarea
              class="form-control"
              id="exampleFormControlTextarea1"
              onChange={handleChange}
              name="content"
              placeholder="Copy and Paste the Ingredients of the Sunscreen"
              rows="10"
              value={formData.content}
            ></textarea>
          </div>
          <button onClick={checkIng} class="btn btn-warning d-block mx-auto">
            <h2>Check</h2>
          </button>

          {formData.isTable && <Outputtable ing={formData.output} />}
          {formData.isSafe && <Safealert />}
        </form>
      </div>

      {formData.isTable | formData.isSafe && (
        <div class="mb-3 row justify-content-center">
          <div class="col-md-6 mt-5 ">
            <label for="exampleFormControlInput1" class="form-label">
              Basic Product Info
            </label>
            <input
              type="brand"
              class="form-control mt-2"
              placeholder="Product Brand"
            />
            <input
              type="Name"
              class="form-control mt-2"
              placeholder="Product Name"
            />
            <input type="spf" class="form-control mt-2" placeholder="SPF" />
            <input
              type="imglink"
              class="form-control mt-2"
              placeholder="Product Img Link"
            />
            <button className="btn btn-success">Add</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default InputArea;
