import React, { useState } from "react";

function InputArea() {
  const [content, addContent] = useState({ content: "" });

  function handleChange(event) {
    const newValue = event.target.value;

    addContent((prevValue) => {
      return {
        ...prevValue,
        newValue,
      };
    });
    console.log(content);
  }

  function checkIng(event) {
    const unsafeInglist = [
      "Oxybenzone",
      "Octinoxate",
      "Octocrylene",
      "Homosalate",
      "PABA",
      "Parabens",
      "Triclosan",
    ];
    let output = "";

    for (let ingredient of unsafeInglist) {
      if (content.includes(ingredient)) {
        output += ingredient + " is unsafe ";
      }
    }

    event.preventDefault();
    return output;
  }
  return (
    <div>
      <form>
        <textarea
          onChange={handleChange}
          name="content"
          placeholder="Take a note..."
          rows="10"
          value={content}
        />
        <button onClick={checkIng}>Add</button>
      </form>
    </div>
  );
}

export default InputArea;
