import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function ProductDetail() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Fetch product data using the product ID
    const fetchProduct = async () => {
      try {
        const response = await fetch(`http://localhost:3001/products/${id}`);
        const productData = await response.json();
        setProduct(productData);
      } catch (error) {
        console.error("Error fetching product data", error);
      }
    };

    fetchProduct();
  }, [id]);

  if (!product) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>{product.title}</h2>
      <p>Brand: {product.brand}</p>

      <div class="container">
        <div class="col-lg-8 border p-3 main-section bg-white">
          <div class="row hedding m-0 pl-3 pt-0 pb-3">
            Product Detail Design Using Bootstrap 4.0
          </div>
          <div class="row m-0">
            <div class="col-lg-4 left-side-product-box pb-3">
              <img
                src="http://nicesnippets.com/demo/pd-image1.jpg"
                class="border p-3"
              />
            </div>
            <div class="col-lg-8">
              <div class="right-side-pro-detail border p-3 m-0">
                <div class="row">
                  <div class="col-lg-12">
                    <span>Who What Wear</span>
                    <p class="m-0 p-0">Women's Velvet Dress</p>
                  </div>
                  <div class="col-lg-12">
                    <p class="m-0 p-0 price-pro">$30</p>
                    <hr class="p-0 m-0" />
                  </div>
                  <div class="col-lg-12 pt-2">
                    <h5>Product Detail</h5>
                    <span>
                      Lorem ipsum dolor sit amet, consectetur adipisicing elit,
                      sed do eiusmod tempor incididunt ut labore et dolore magna
                      aliqua. Ut enim ad minim veniam, quis nostrud exercitation
                      ullamco laboris.
                    </span>
                    <hr class="m-0 pt-2 mt-2" />
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div class="row">
            <div class="col-lg-12 text-center pt-3">
              <h4>More Product</h4>
            </div>
          </div>
          <div class="row mt-3 p-0 text-center pro-box-section">
            <div class="col-lg-3 pb-2">
              <div class="pro-box border p-0 m-0">
                <img src="http://nicesnippets.com/demo/pd-b-image1.jpg" />
              </div>
            </div>
            <div class="col-lg-3 pb-2">
              <div class="pro-box border p-0 m-0">
                <img src="http://nicesnippets.com/demo/pd-b-images2.jpg" />
              </div>
            </div>
            <div class="col-lg-3 pb-2">
              <div class="pro-box border p-0 m-0">
                <img src="http://nicesnippets.com/demo/pd-b-images3.jpg" />
              </div>
            </div>
            <div class="col-lg-3 pb-2">
              <div class="pro-box border p-0 m-0">
                <img src="http://nicesnippets.com/demo/pd-b-images4.jpg" />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
