import React, { useEffect, useState } from "react";

function Procards() {
  const [list, setList] = useState([]);
  //get all items
  const getList = async () => {
    try {
      const response = await fetch("http://localhost:3001/products", {
        method: "GET",
        headers: { "Content-Type": "application/json" },
      });
      console.log(response);
      const jsonDate = await response.json();
      console.log(jsonDate);
      setList(jsonDate);
    } catch (error) {
      console.log(error.message);
    }
  };
  useEffect(() => {
    getList();
  }, []);
  return (
    <div className=" px-4 py-5">
      <h2 class="container pb-2 border-bottom">Products</h2>
      <div class="row">
        <div class="col-xl-3 col-lg-4 col-md-6 mb-4">
          <div class="bg-white rounded shadow-sm">
            <img
              src="https://bootstrapious.com/i/snippets/sn-gallery/img-1.jpg"
              alt=""
              class="img-fluid card-img-top"
            />
            <div class="p-4">
              <h5>
                {" "}
                <a href="/" class="text-dark">
                  Red paint cup
                </a>
              </h5>
              <p class="small text-muted mb-0">
                Lorem ipsum dolor sit amet, consectetur adipisicing elit
              </p>
              <div class="d-flex align-items-center justify-content-between rounded-pill bg-light px-3 py-2 mt-4">
                <p class="small mb-0">
                  <i class="fa fa-picture-o mr-2"></i>
                  <span class="font-weight-bold">JPG</span>
                </p>
                <div class="badge badge-primary px-3 rounded-pill font-weight-normal">
                  Trend
                </div>
              </div>
            </div>
          </div>
        </div>

        <div class="row justify-content-center mt-5">
          {list.slice(0, 4).map((item) => (
            <div class="col-sm-2 mb-3 mb-sm-0 ">
              <div class="card">
                <div
                  className={`card-header ${
                    item.is_safe ? " text-bg-info" : "text-bg-danger"
                  }`}
                >
                  {item.is_safe ? " Safe " : "Danger"}
                </div>
                <img
                  src={item.link}
                  class="card-img-top fixed-img"
                  alt={item.title}
                ></img>
                <div class="card-body">
                  <h5 class="card-title">{item.title}</h5>
                  <span class="card-text">{item.brand}</span>
                </div>
                <div class="card-footer">
                  <nav aria-label="Page navigation example">
                    <ul class="pagination justify-content-center">
                      <li class="page-item disabled">
                        <a class="page-link" href="/">
                          SPF: {item.spf}
                        </a>
                      </li>

                      <li class="page-item">
                        <a class="page-link" href="/">
                          more
                        </a>
                      </li>
                    </ul>
                  </nav>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Procards;
