import React, { useState } from "react";
import Outputtable from "./Outputtable";

function InputArea() {
  const [formData, setFormData] = useState({
    content: "",
    output: "",
    isTable: false,
  });

  function handleChange(event) {
    const newValue = event.target.value.toLowerCase();
    setFormData((prevData) => ({ ...prevData, content: newValue }));
  }

  async function checkIng(event) {
    event.preventDefault();

    const unsafeInglist = [
      "oxybenzone",
      "octinoxate",
      "octocrylene",
      "homosalate",
      "paba",
      "parabens",
      "triclosan",
    ];

    let unsafeIngredients = [];

    for (let ingredient of unsafeInglist) {
      if (formData.content.includes(ingredient)) {
        unsafeIngredients.push(ingredient);
      }
    }

    if (unsafeInglist.length > 0) {
      setFormData((prevData) => ({ ...prevData, isTable: true }));
    }

    setFormData((prevData) => ({ ...prevData, output: unsafeIngredients }));
  }

  return (
    <div class="row justify-content-center">
      <div class="col-md-6 mt-5 ">
        <form>
          <div class="mb-3">
            <textarea
              class="form-control"
              id="exampleFormControlTextarea1"
              onChange={handleChange}
              name="content"
              placeholder="Copy and Paste the Ingredients of the Sunscreen"
              rows="10"
              value={formData.content}
            ></textarea>
          </div>
          <button onClick={checkIng} class="btn btn-warning">
            <h2>Check</h2>
          </button>
        </form>
        <p>{formData.output}</p>
        {formData.isTable && <Outputtable />}
      </div>
    </div>
  );
}

export default InputArea;
