import React, { useEffect, useState } from "react";
import Item from "./Item";

function Products() {
  const [list, setList] = useState([]);
  //get all items
  const getList = async () => {
    try {
      const response = await fetch("http://localhost:3001/products", {
        method: "GET",
        headers: { "Content-Type": "application/json" },
      });
      console.log(response);
      const jsonDate = await response.json();
      console.log(jsonDate);
      setList(jsonDate);
    } catch (error) {
      console.log(error.message);
    }
  };
  useEffect(() => {
    getList();
  }, []);

  return (
    <div>
      <h2>Products</h2>

      {list.map((item, index) => (
        <Item
          key={index}
          title={item.title}
          link={item.link}
          alt={item.title}
        />
      ))}
      <div class="media d-block d-sm-flex text-center text-sm-left">
        <a class="cart-item-thumb mx-auto mr-sm-4" href="#">
          <img
            src="https://www.bootdey.com/image/240x240/FF0000/000000"
            alt="Product"
          />
        </a>
        <div class="media-body pt-3">
          <h3 class="product-card-title font-weight-semibold border-0 pb-0">
            <a href="#">Calvin Klein Jeans Keds</a>
          </h3>
          <div class="font-size-sm">
            <span class="text-muted mr-2">Size:</span>8.5
          </div>
          <div class="font-size-sm">
            <span class="text-muted mr-2">Color:</span>Black
          </div>
          <div class="font-size-lg text-primary pt-2">$125.00</div>
        </div>
      </div>
    </div>
  );
}

export default Products;
