import React, { useState } from "react";
import Outputtable from "./Outputtable";

function InputArea() {
  const [formData, setFormData] = useState({
    content: "",
    output: "",
    isTable: false,
    isSafe: false,
  });

  const [hasChecked, setHasChecked] = useState(false);

  function handleChange(event) {
    const newValue = event.target.value.toLowerCase();
    setFormData((prevData) => ({
      ...prevData,
      content: newValue,
    }));
  }

  async function checkIng(event) {
    event.preventDefault();

    const unsafeInglist = [
      "oxybenzone",
      "octinoxate",
      "octocrylene",
      "homosalate",
      "parabens",
      "triclosan",
      "para aminobenzoic acid",
      "methylbenzylidene camphor",
      "avobenzone",
    ];

    let unsafeIngredients = [];

    for (let ingredient of unsafeInglist) {
      if (formData.content.includes(ingredient)) {
        unsafeIngredients.push(ingredient);
      }
    }
    setHasChecked(true);

    if (unsafeInglist.length > 0) {
      setFormData((prevData) => ({
        ...prevData,
        isTable: true,
      }));
    } else {
      setFormData((prevData) => ({
        ...prevData,
        isSafe: true,
      }));
    }

    setFormData((prevData) => ({ ...prevData, output: unsafeIngredients }));
  }
  // Reset isTable and isSafe when the user clicks "Check" again
  function resetCheck() {
    setHasChecked(false);
    setFormData((prevData) => ({
      ...prevData,
      isTable: false,
      isSafe: false,
    }));
  }
  return (
    <div class="row justify-content-center">
      <div class="col-md-6 mt-5 ">
        <form>
          <div class="mb-3">
            <textarea
              class="form-control"
              id="exampleFormControlTextarea1"
              onChange={handleChange}
              name="content"
              placeholder="Copy and Paste the Ingredients of the Sunscreen"
              rows="10"
              value={formData.content}
            ></textarea>
          </div>
          <button onClick={checkIng} class="btn btn-warning d-block mx-auto">
            <h2>Check</h2>
          </button>
          {formData.unsafeIngredients}
          {formData.isTable && <Outputtable ing={formData.output} />}
          {formData.isSafe && <h1>safe!</h1>}
        </form>
      </div>

      {formData.isTable | formData.isSafe && <p>test</p>}
    </div>
  );
}

export default InputArea;
