import React, { useEffect, useState } from "react";

function Ingcards() {
  const [list, setList] = useState([]);
  //get all items
  const getList = async () => {
    try {
      const response = await fetch("http://localhost:3001/ingredients", {
        method: "GET",
        headers: { "Content-Type": "application/json" },
      });
      console.log(response);
      const jsonDate = await response.json();
      console.log(jsonDate);
      setList(jsonDate);
    } catch (error) {
      console.log(error.message);
    }
  };
  useEffect(() => {
    getList();
  }, []);
  return (
    <div>
      <h2 class="container pb-2 border-bottom">Ingredients</h2>
      <div class="row mt-5">
        {list.map((item) => (
          <div class="col-xl-3 col-lg-4 col-md-6 mb-4">
            <div class="bg-white rounded shadow-sm fixed-img">
              <img
                src={item.imglink}
                alt={item.name}
                class="img-fluid card-img-top"
              />
              <div class="p-4">
                <h5>
                  <a href="/" class="text-dark">
                    {item.name}
                  </a>
                </h5>
                <p class="small text-muted mb-0">{item.impact}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Ingcards;
