import React, { useState } from "react";

function InputArea() {
  const [content, setContent] = useState("");
  const [output, setOutput] = useState("");

  function handleChange(event) {
    const newValue = event.target.value;
    setContent(newValue);
    console.log(content);
  }

  function checkIng(event) {
    const unsafeInglist = [
      "Oxybenzone",
      "Octinoxate",
      "Octocrylene",
      "Homosalate",
      "PABA",
      "Parabens",
      "Triclosan",
    ];

    let unsafeIngredients = "";

    for (let ingredient of unsafeInglist) {
      if (content.includes(ingredient)) {
        unsafeIngredients += ingredient + " is unsafe ";
      }
    }

    setOutput(unsafeIngredients);
    event.preventDefault();
  }

  return (
    <div>
      <form>
        <textarea
          onChange={handleChange}
          name="content"
          placeholder="Copy and Paste the Ingredients of the Sunscreen"
          rows="10"
          value={content}
        />
        <button onClick={checkIng}>Check</button>
      </form>
      <p>{output}</p>
    </div>
  );
}

export default InputArea;
